import React from "react"

function List(props){

    // making Driver List
    const makeDriverList = props.driverList.map((obj, index) => {
        return (
            <li key={index}>{obj.name} - {obj.address}</li>
        )
    })
    const makeRideList = props.rideList.map((obj, index) => {
        return (
            <li key={index}>{obj.name} - {obj.address}</li>
        )
    })

    const assignDriverList = props.driverList.map((obj, index) => {
        const placePassenger = props.assignDriver.map((object, index) => {  
            if(object.driver === obj.name){
                return(
                    <li>{object.passenger}</li>
                )
            }
            })
            return (
                <div className="listComponent">
                    <h4 className="listTitles" key={index}>{obj.name}</h4>
                    <ul>
                        {placePassenger}
                    </ul>
                </div>
            )
    })
    return(
        <div className="information">
            <header>
                <h2>List</h2>
            </header>
            <div className="list">
                <div className="fullList">
                    <h3 className="listTitles">Drivers:</h3>
                    <ul>
                        {makeDriverList}
                    </ul>
                    <h3 className="listTitles">Passengers:</h3>
                    <ul>
                        {makeRideList}
                    </ul>
                </div>
                <div className="assignWithTitle">
                    <h3>Assigned Passengers to Drivers:</h3>
                    <div className="assign">
                        {assignDriverList}
                    </div>
                </div>
            </div>
        </div>

    )
}
export default List
