import React, {useState} from 'react';


const DriverMarker = (props) => {
    //settin states
    const [show, setShow] = useState(false)
    const [displayNames, setDisplayNames] = useState(false)

    //display passenger list
    const passengerList = props.rideList.map((object, index) => {
        return(
            <a><li>{object.name}</li></a>
        )
    })

    //setting functions
    const _onClick = () => {
        setShow(!show)
    }
    const assignPass = (name) => {
        console.log("Assign passengers to ", name)
        setDisplayNames(!displayNames)
    }
    return (
       <React.Fragment>
           <div>
                <img
                className="dmarker"
                src="https://storage.needpix.com/rsynced_images/icon-2070748_1280.png"
                style={{ cursor: 'pointer'}}
                onClick={_onClick} />
             </div>   
            {/* Below is info window component */}
            {show && (
            <div
            className="infoDBox"
                ><span>{props.driverList.name}</span><br/>
                {props.driverList.address} 
                <p onClick={()=> assignPass(props.driverList.name)}>Assign<div className="plusSign">+</div></p></div>
                )}
            {displayNames && (
                <div className="passengerList">
                    <ul>
                        {passengerList}
                    </ul>
                </div>
                )}    

        </React.Fragment>
        )
  };
  export default DriverMarker
