import React, {useState} from 'react';


const RideMarker = (props) => {
    const [show, setShow] = useState(false)

    //display driver list
    const passengerList = props.rideList.map((object, index) => {
        return(
            <a><li>{object.name}</li></a>
        )
    })
    const _onClick = () => {
        setShow(!show)
    }
    return (
       <React.Fragment>
            <div
                className="rmarker"
                style={{ cursor: 'pointer'}}
                onClick={_onClick} />
            {/* Below is info window component */}
            {show && (
            <div
            className="infoRBox"
                ><span>{props.rideList.name}</span><br/>
                {props.rideList.address}</div>
                )}
        </React.Fragment>
        )
  };
  export default RideMarker
