import React, {useState} from 'react';


const RideMarker = (props) => {
    const [show, setShow] = useState(false)
    const [displayNames, setDisplayNames] = useState(false)

    //display driver list
    const driverList = props.driverList.map((object, index) => {
        return(
            <a><li>{object.name}</li></a>
        )
    })

    //function 
    const _onClick = () => {
        setShow(!show)
    }
    const assignPass = (name) => {
        setDisplayNames(!displayNames)
    }

    return (
       <React.Fragment>
            <div
                className="rmarker"
                style={{ cursor: 'pointer'}}
                onClick={_onClick} />
            {/* Below is info window component */}
            {show && (
            <div
            className="infoRBox"
                ><span>{props.rideList.name}</span><br/>
                {props.rideList.address}
                <p onClick={()=> assignPass(props.driverList.name)}>Assign <div className="plusSign">+</div></p></div>
                )}
            {displayNames && (
                <div className="passengerList">
                    <ul>
                        {driverList}
                    </ul>
                </div>
            )}       
        </React.Fragment>
        )
  };
  export default RideMarker
