import React from 'react';


const RideMarker = (props) => {
    const [show, setShow] = useState(false)
    const _onClick = () => {
        console.log("I just got clicked")
        setShow(!show)
    }
    return (
       <React.Fragment>
            <div
                className="rmarker"
                style={{ cursor: 'pointer'}}
                onClick={_onClick} />
            {/* Below is info window component */}
            {show && (
            <div
            className="infoBox"
                style={{
                    width: 100,
                    height: 100
                }}
                >Info window</div>
                )}
        </React.Fragment>
        )
  };
  export default RideMarker
