import React, {useState} from 'react';


const RideMarker = (props) => {
    const [show, setShow] = useState(false)

    //function 
    const _onClick = () => {
        setShow(!show)
    }
    const assignPass = (name) => {
        props.assignPass(name);
    }

    return (
       <React.Fragment>
            <div
                className="rmarker"
                style={{ cursor: 'pointer'}}
                onClick={_onClick} />
            {/* Below is info window component */}
            {show && (
            <div
            className="infoRBox"
                ><span>{props.rideList.name}</span><br/><br/>
                {props.rideList.address}
                <p onClick={()=> assignPass(props.rideList.name)}>Assign <div className="plusSign">+</div></p></div>
                )}     
        </React.Fragment>
        )
  };
  export default RideMarker
