import React, {useState} from 'react';


const RideMarker = (props) => {
    const [show, setShow] = useState(false)
    console.log("here is my show", show)
    if(show === true){
        return (
            <>
            <div
                style={{
                width: 100,
                height: 100
                }}
            >Info window</div> 
             <div
                className="rmarker"
                onClick= {setShow(!show)}
                style={{ cursor: 'pointer'}} />
            </>    
            )
    } else {
    return (
        <div
        className="rmarker"
        onClick= {()=>{setShow(!show)}}
        style={{ cursor: 'pointer'}} />
       )
    }
  };
  export default RideMarker
