import React, { useState } from 'react';
import Marker from "./Marker"
import Form from "./Form"
import Extra from "./Extra"
import GoogleMapReact from 'google-map-react';


const SimpleMap = () => {
    const getMapOptions = () => {
      return {
        disableDefaultUI: true,
        mapTypeControl: true,
        streetViewControl: true,
        styles: [{ featureType: 'poi', elementType: 'labels', stylers: [{ visibility: 'on' }] }],
      };
    };
    const [centerC, setCenter] = useState({lat: 11.0168, lng: 76.9558 });
    const [zoom, setZoom] = useState(11);
    const [address, setAddress] = useState([{}])
    const [latC, setLat] = useState("")
    const [lngC, setLng] = useState("")

    const setCoordinates = (later , longer)=>{
        console.log("coordinates", later, longer)
        setCenter({lat: later, lng: longer})
        setLat(later)
        letLng(longer)
        // setAddress([...address, {lat: later, lng: longer}])
        // console.log("address", address)
      } 

    return (
        <div id="map" style={{ height: '90vh', width: '100%' }}>
        <Form coordinates={setCoordinates}/>
        <GoogleMapReact
          bootstrapURLKeys={{ key:'AIzaSyDJ56l2Y_6K3vN5rH30aKddRVljnEsuR_Y' }}
          center= {centerC}
          defaultZoom={zoom}
        //   options = {getMapOptions}
        > 
        <Extra lat={11.0168} lng={76.9558} text={'A'} />
        <Marker lat={latC} lng={lngC}/>
        </GoogleMapReact>
        </div>
    );
}

    export default SimpleMap
