import React, {useState} from "react"
import Form from "./Form"
import { withGoogleMap, GoogleMap, withScriptjs, InfoWindow, Marker } from "react-google-maps"
import Geocode from "react-geocode"


Geocode.setApiKey("AIzaSyDJ56l2Y_6K3vN5rH30aKddRVljnEsuR_Y");
Geocode.enableDebug();


function MapContainer (){
  const [markers, setMarkers] = useState([<Marker position={{ lat: 37.4275, lng: -122.1697}}/>])
  const [latC, setLat] = useState("")
  const [lngC, setLng] = useState("")

  //setting coordinates for address
  const setCoordinates = (lat , lng)=>{
    setLat(lat)
    console.log(lat)
    setLng(lng)
    console.log(lng)
    setMarkers([...markers, <Marker position={{ lat: latC, lng: lngC}}/>])
  } 



  const GoogleMapExample = withGoogleMap(props => (
      <GoogleMap
        defaultCenter = { { lat: 37.4275, lng: -122.1697 } }
        defaultZoom = { 13 }
      >
        {markers}
      </GoogleMap>
    ));
  return(
    <div className="map" style={{ height: `90vh`, width: '100%' }}>
        <Form coordinates={setCoordinates}/>
      {/* <img src="https://upload.wikimedia.org/wikipedia/commons/1/17/Ancient_Egypt_map-en.svg" style={{ height: `90vh`, width: '100%' }}></img> */}
      <GoogleMapExample
        containerElement={ <div style={{ height: `80vh`, width: '100%', marginTop: `40px` }} /> }
        mapElement={ <div style={{ height: `80vh`, marginTop: `40px`}} /> }
      />
    </div>
  );
}
export default MapContainer;
