import React, {useState} from 'react';


const DriverMarker = (props) => {
    const [show, setShow] = useState(false)
    const _onClick = () => {
        setShow(!show)
    }
    return (
       <React.Fragment>
           <div>
                <img
                className="dmarker"
                src="https://storage.needpix.com/rsynced_images/icon-2070748_1280.png"
                style={{ cursor: 'pointer'}}
                onClick={_onClick} />
             </div>   
            {/* Below is info window component */}
            {show && (
            <div
            className="infoDBox"
                ><span>{props.driverList.name}</span><br/>
                {props.driverList.address} 
                <p>Assign Passengers<a><div className="plus sign">+</div></a></p></div>
                )}
        </React.Fragment>
        )
  };
  export default DriverMarker
