import React, {useState} from 'react';


const RideMarker = (props) => {
    const [show, setShow] = useState(false)
    if(show === true){
        console.log("I am true!")
        return (
            <>
            <div className="infoBox"
                style={{
                width: "100px",
                height: "100px"
                }}
            >Info window</div> 
             <div
                className="rmarker"
                onClick= {setShow(!show)}
                style={{ cursor: 'pointer'}} />
            </>    
            )
    } else {
    return (
        <div
        className="rmarker"
        onClick= {()=>{setShow(!show)}}
        style={{ cursor: 'pointer'}} />
       )
    }
  };
  export default RideMarker
